import React from "react";
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Badge from '@mui/material/Badge';
import { withStyles } from '@mui/material/styles';
import { Product } from "../data";

interface BasketProps {
    basketItem: Product[] | null;
}

const useStyles = makeStyles((theme) => ({
    badge: {
        right: -3,
        top: 13,
        border: `2px solid ${theme.palette.background.paper}`,
        padding: '0 4px',
    },
}));

const Basket: React.FC<BasketProps> = ({ basketItem }) => {
    const handleClick = () => {
        alert('Basket Stuff');
    };

    const basketItemCount = basketItem ? basketItem.length : 0;

    return (
        <StyledBadge badgeContent={basketItemCount} color="secondary">
            <ShoppingCartIcon fontSize="large" onClick={handleClick} />
        </StyledBadge>
    );
};

export default Basket;
