import React, { useState, useEffect } from 'react';
import './App.css';
import { Product, products as productData } from './data';
import ProductCard from "./components/ProductCards";
import ProductDialog from "./components/ProductDialog";
import SearchBar from "./components/SearchBar";
import Filter from "./components/Filter";
import Basket from "./components/Basket";

function App() {
    const [products, setProducts] = useState<Product[]>([]);
    const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
    const [basketItem, setBasket] = useState<Product[] | null >(null);

    useEffect(() => {
        setProducts(productData);
    }, []);

    const openModal = (product: Product) => {
        setSelectedProduct(product);
    };

    const closeModal = () => {
        setSelectedProduct(null);
    };

    return (
        <div className="App">

            <div className="search-bar">
                <SearchBar/>
                <Filter />
                <Basket basket = {basketItem} />
            </div>
            <ProductList products={products} openModal={openModal} />
            <ProductDialog
                product={selectedProduct}
                isOpen={selectedProduct !== null}
                onClose={closeModal}
            />

        </div>
    );
}

interface ProductListProps {
    products: Product[];
}

interface ProductListProps {
    products: Product[];
    openModal: (product: Product) => void;
}

function ProductList({ products, openModal }: ProductListProps) {
    return (
        <div className="products">
            {products.map(product => (
                <ProductCard
                    key={product.id}
                    product={product}
                    onClick={() => openModal(product)}
                />
            ))}
        </div>
    );
}


export default App;
