import React, {useState} from 'react';
import {Dialog, DialogTitle, DialogContent, DialogActions, Button, Checkbox} from '@mui/material';
import {Product} from "../data.ts";


interface BasketDialogProps {
    isOpen: boolean;
    onClose: () => void;
    checkedState: {}
    setProduct: React.Dispatch<React.SetStateAction<Product[]>>;
    setFilters: React.Dispatch<React.SetStateAction<{ stocked: boolean }> | undefined>;
}

const FilterDialog: React.FC<BasketDialogProps> = ({ isOpen, onClose, checkedState}) => {
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const { name, checked } = event.target;
        setFilters(prevFilters => ({
            ...prevFilters,
            [name]: checked
        }));
    };

    const applyFilters = () => {
        let filteredProducts = productData;

        if (filters.inStock) {
            filteredProducts = filteredProducts.filter(product => product.stocked);
        }

        // Add more filter conditions here as needed

        setProduct(filteredProducts);
    };

    return (
        <Dialog open={isOpen} onClose={onClose}>

            <DialogTitle>Filters</DialogTitle>
            <DialogContent dividers>
                <Checkbox onChange={handleChange(Product.stocked)}/> In Stock
                <Checkbox />
            </DialogContent>
            <DialogActions>
                <Button onClick={onClose} color="primary">
                    Back
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default FilterDialog;
