import React from "react";
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Badge from '@mui/material/Badge';
import { Product } from "../data";
import { makeStyles, Theme } from '@mui/material/styles';

interface BasketProps {
    basketItem: Product[] | null;
    setShoppingBasketOpen: React.Dispatch<React.SetStateAction<boolean>>;
}


const Basket: React.FC<BasketProps> = ({ basketItem, setShoppingBasketOpen }) => {

    const handleClick = () => {
        setShoppingBasketOpen(true);
    };

    const basketItemCount = basketItem ? basketItem.length : 0;

    return (
        <div style={{
            display: 'flex',
            alignItems: 'center',
            flexWrap: 'wrap',
        }}>
            <ShoppingCartIcon onClick={handleClick} />
            <span style={{color: 'red'}}>{basketItemCount}</span>
        </div>
    );
};

export default Basket;
