import React from 'react';
import {Dialog, DialogTitle, DialogContent, DialogActions, Button, Checkbox} from '@mui/material';
import {Product, products as productData} from '../data';


interface FilterDialogProps {
    isOpen: boolean;
    onClose: () => void;
    setProducts: React.Dispatch<React.SetStateAction<Product[]>>;
    setFilters: React.Dispatch<React.SetStateAction<{ stocked: boolean, search: string | null  }>>;
    filters: { stocked: boolean, search: string | null };
}

const FilterDialog: React.FC<FilterDialogProps> = ({ isOpen, onClose, setProducts, setFilters, filters }) => {
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const { name, checked } = event.target;
        setFilters(prevFilters => ({
            ...prevFilters,
            [name]: checked
        }));
        applyFilters()
    };

    const applyFilters = () => {
        let filteredProducts = productData;

        if (!filters.stocked) {
            filteredProducts = filteredProducts.filter(a => a.stocked &&
                a.name.toLowerCase().includes(filters.search.toLowerCase()) );
        }

        // Apply more filters as needed here

        setProducts(filteredProducts);
    };

    return (
        <Dialog open={isOpen} onClose={onClose}>
            <DialogTitle>Filters</DialogTitle>
            <DialogContent dividers>
                <Checkbox name="stocked" checked={filters.stocked} onChange={handleChange} /> In Stocked
            </DialogContent>
            <DialogActions>
                <Button onClick={onClose} color="primary">
                    Back
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default FilterDialog;
