import React from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography } from '@mui/material';
import { Product } from '../data';
import classes from "*.module.css";

interface BasketDialogProps {
    basket: Product[] | null;
    isOpen: boolean;
    onClose: () => void;
    setBasket: React.Dispatch<React.SetStateAction<Product[] | null>>;
}

const BracketDialog: React.FC<BasketDialogProps> = ({ basket, isOpen, onClose, setBasket }) => {
    const add = () => {
        if (basket) {
            setBasket(prevBasket => prevBasket ? [...prevBasket, basket] : [basket]);
            alert(`${basket.name} added to basket!`);
        }
    };
    if (!basket) return null
    return (
        <Dialog open={isOpen} onClose={onClose}>
            <DialogTitle>{basket.name}</DialogTitle>
            <DialogContent dividers>
                <Typography className='mini-image-container'>
                    <img src={basket.image} alt={basket.name} className="mini-image" />
                </Typography>
                <Typography gutterBottom>
                    {basket.description}
                </Typography>

            </DialogContent>
            <DialogActions>
                <Button onClick={add}>
                    Add to Basket
                </Button>

                <Button onClick={onClose} color="primary">
                    Back
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default BracketDialog;
