import React, { useState, useEffect } from 'react';
import './App.css';
import { Product, products as productData } from './data';

function App() {
    const [products, setProducts] = useState<Product[]>([]);

    useEffect(() => {
        // Simulate an API call
        setProducts(productData);
    }, []);

    return (
        <div className="App">
            <nav className="navbar">
                <ul>
                    <li><a href="#home">Home</a></li>
                    <li><a href="#about">About</a></li>
                    <li><a href="#contact">Contact</a></li>
                </ul>
            </nav>

            <div className="search-bar">
                <SearchBar/>
                <Filter />
            </div>
            <ProductList products={products} />
        </div>
    );
}

function SearchBar() {
    const [query, setQuery] = useState('');

    function onChange() {

    }

    return <input type="text" onChange={onChange} value={query} placeholder="Search..." />;
}

function Filter() {
    function onClick() {
        alert('Filter option');
    }

    return <button onClick={onClick}>Filter</button>;
}

interface ProductListProps {
    products: Product[];
}

function ProductList({ products }: ProductListProps) {
    return (
        <div className="products">
            {products.map(product => (
                <ProductCard key={product.id} product={product}/>
            ))}
        </div>
    );
}

interface ProductCardProps {
    product: Product;
}

function ProductCard({product}: ProductCardProps){
    const [isHovered, setIsHovered] = useState(false);

    function handleMouseEnter () {
        setIsHovered(true);
    }

    function handleMouseLeave(){
        setIsHovered(false);
    }
    function handleClick () {
        alert("You have clicked on " + product.name)
    }

    return (
        <div
            className={`card ${isHovered ? 'hovered' : ''}`}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            onClick={handleClick}
        >
            <h3>{product.name}</h3>
            <p>{product.description}</p>
        </div>
    );
}

interface ProductDialogProps {
    product: Product;
    onClose: () => void;
}

const ProductDialog: React.FC<ProductDialogProps> = ({ product, onClose }) => {
    return (
        <div className="dialog-overlay">
            <div className="dialog">
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <button onClick={onClose}>Close</button>
            </div>
        </div>
    );
};

export default App;
