import React from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography } from '@mui/material';
import { Product } from '../data';
import classes from "*.module.css";
import ProductCard from "./ProductCards";

interface BasketDialogProps {
    basketItems: Product[] | null;
    isOpen: boolean;
    onClose: () => void;
    setBasket: React.Dispatch<React.SetStateAction<Product[] | null>>;
}

const BracketDialog: React.FC<BasketDialogProps> = ({ basketItems, isOpen, onClose, setBasket }) => {
    if (!basketItems) return null
    const removeItem = (basketId: number) => {
        if (!basketItems) return; // Exit early if basketItems is null

        // Use optional chaining (?.) to safely access prevBasket
        setBasket(prevBasket => prevBasket?.filter(b => b.id !== basketId));
    };
    return (
        <Dialog open={isOpen} onClose={onClose}>

            <DialogTitle>Items in Basket</DialogTitle>
            <DialogContent dividers>
                {basketItems.map((basket) => (
                    <div key={basket.id} style={{ marginBottom: '10px' }}>
                        <Typography className='mini-image-container'>
                            <img src={basket.image} alt={basket.name} className="mini-image" />
                        </Typography>
                        <Typography gutterBottom>
                            {basket.description}
                        </Typography>
                        <Button onClick={() => removeItem(basket.id)} color="primary">
                            Remove
                        <Button/>
                            Remove
                        </Button>
                    </div>
                ))}


            </DialogContent>
            <DialogActions>
                <Button onClick={onClose} color="primary">
                    Back
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default BracketDialog;
