import React, { useState, useEffect } from 'react';
import './App.css';
import { Product, products as productData } from './data';
import ProductCard from "./components/ProductCards";
import ProductDialog from "./components/ProductDialog";
import SearchBar from "./components/SearchBar";
import Filter from "./components/Filter";

const App: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);

    useEffect(() => {
        // Simulate an API call
        setProducts(productData);
    }, []);

    const openModal = (product: Product) => {
        setSelectedProduct(product);
    };

    const closeModal = () => {
        setSelectedProduct(null);
    };

    return (
        <div className="App">
            <nav className="navbar">
                <ul>
                    <li><a href="#home">Home</a></li>
                    <li><a href="#about">About</a></li>
                    <li><a href="#contact">Contact</a></li>
                </ul>
            </nav>

            <div className="search-bar">
                {/* You can add SearchBar and Filter components here */}
            </div>

            <ProductList products={products} openModal={openModal} />

            {/* Only render ProductDialog if selectedProduct is not null */}
            {selectedProduct !== null && (
                <ProductDialog
                    product={selectedProduct}
                    isOpen={true}
                    onClose={closeModal}
                />
            )}
        </div>
    );
};

export default App;
