import React, { useState, useEffect } from 'react';
import './App.css';
import {BasketItem, Product, products as productData} from './data';
import ProductCard from "./components/ProductCards";
import ProductDialog from "./components/ProductDialog";
import SearchBar from "./components/SearchBar";
import Filter from "./components/Filter";
import Basket from "./components/Basket";
import BasketDialog from "./components/BasketDialog";
import FilterDialog from "./components/FilterDialog";

function App() {
    const [products, setProducts] = useState<Product[]>([]);
    const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
    const [basketItems, setBasket] = useState<BasketItem[] | null >(null);
    const [shoppingBasketOpen, setShoppingBasketOpen] = useState(false);
    const [filterOpen, setFilterOpen] = useState<boolean>(false);
    const [filters, setFilters] = useState<{ stocked: boolean, search: string}>({ stocked: false, search: "" });

    useEffect(() => {
        setProducts(productData);
    }, []);

    const applyFilters = () => {
        let filteredProducts = productData;

        if (filters.stocked) {
            filteredProducts = filteredProducts.filter(a => a.stocked);
        }

        // Apply more filters as needed here
        filteredProducts = filteredProducts.filter(a => a.name.toLowerCase().includes(filters.search.toLowerCase()) );
        setProducts(filteredProducts);
    };

    const openModal = (product: Product) => {
        setSelectedProduct(product);
    };

    const closeModal = () => {
        setSelectedProduct(null);
    };

    const closeBasketModal = () => {
        setShoppingBasketOpen(false);
    };
    const closeFilterModal = () => {
        setFilterOpen(false);
    };
    return (
        <div className="App">

            <div className="search-bar">
                <SearchBar
                    products={products}
                    setProduct={setProducts}
                    setFilters={setFilters}
                    applyFilters={applyFilters}
                />
                <Filter
                    setFilter={setFilterOpen}
                />
                <Basket
                    basketItem = {basketItems}
                    setShoppingBasketOpen={setShoppingBasketOpen}
                />
            </div>
            <ProductList products={products} openModal={openModal} />
            <ProductDialog
                product={selectedProduct}
                isOpen={selectedProduct !== null}
                onClose={closeModal}
                setBasket={setBasket}
            />

            <BasketDialog
                basketItems={basketItems}
                isOpen={shoppingBasketOpen}
                onClose={closeBasketModal}
                setBasket={setBasket}
            />

            <FilterDialog
                isOpen={filterOpen}
                onClose={closeFilterModal}
                filters={filters}
                setProducts={setProducts}
                setFilters={setFilters}
                applyFilters={applyFilters}
            />

        </div>
    );
}

interface ProductListProps {
    products: Product[];
}

interface ProductListProps {
    products: Product[];
    openModal: (product: Product) => void;
}

function ProductList({ products, openModal }: ProductListProps) {
    return (
        <div className="products">
            {products.map(product => (
                <ProductCard
                    key={product.id}
                    product={product}
                    onClick={() => openModal(product)}
                />
            ))}
        </div>
    );
}


export default App;
