import React from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography } from '@mui/material';
import {BasketItem, Product, products} from '../data';
import classes from "*.module.css";
import Basket from "./Basket";

interface ProductDialogProps {
    product: Product | null;
    isOpen: boolean;
    onClose: () => void;
    setBasket: React.Dispatch<React.SetStateAction<BasketItem[] | null>>;
}

const ProductDialog: React.FC<ProductDialogProps> = ({ product, isOpen, onClose, setBasket }) => {

    const addProductToBasket = (productToAdd: Product) => {
        setBasket(prevBasket => {
            if (!prevBasket) {
                // If basket is null or undefined, initialize it with the product and count 1
                return [{ product: productToAdd, count: 1 }];
            } else {
                // Check if the product is already in the basket
                const existingItemIndex = prevBasket.findIndex(item => item.product.id === productToAdd.id);

                if (existingItemIndex !== -1) {
                    // If product exists, update its count by creating a new array with updated count
                    const updatedBasket = [...prevBasket];
                    updatedBasket[existingItemIndex].count++;
                    return updatedBasket;
                } else {
                    // If product does not exist, add it to the basket with count 1
                    return [...prevBasket, { product: productToAdd, count: 1 }];
                }
            }
        });
    };

    if (!product) return null
    return (
        <Dialog open={isOpen} onClose={onClose}>
            <DialogTitle>{product.name}</DialogTitle>
            <DialogContent dividers>
                <Typography className='mini-image-container'>
                    <img src={product.image} alt={product.name} className="mini-image" />
                </Typography>
                <Typography gutterBottom>
                    {product.description}
                </Typography>

            </DialogContent>
            <DialogActions>
                <Button onClick={() => addProductToBasket(product)}>
                    Add to Basket
                </Button>

                <Button onClick={onClose} color="primary">
                    Back
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default ProductDialog;
